export default {
	onBoardCustomer: async () => {

		if(appsmith.URL.queryParams.npg == "formSuccess"){
			if(appsmith.store.vayApplicantRole!="")
				{
					await storeValue("customerID",  appsmith.store.vayApplicantRole.substring(0,3).toUpperCase()+appsmith.store.customerIsIndForeign+[...Array(8)].map(() => Math.floor(Math.random() * 17).toString(17)).join(''));
					await storeValue("customerParticipantID",appsmith.store.vayApplicantRole.substring(0,3).toUpperCase()+appsmith.store.customerIsIndForeign+[...Array(8)].map(() => Math.floor(Math.random() * 18).toString(18)).join(''));
				}else{
					showAlert('Customer Registration failed due to incorrect Data');
					return;
				}

			//await storeValue("customerParticipantID",  [...Array(8)].map(() => Math.floor(Math.random() * 18).toString(18)).join(''));

			return updateCustomerIDandPartID.run( 
				 async (response) => {
				 showAlert('Customer ID and Participant ID updated for Customer');
				 await updateBankCustID.run( 
					 		async (response) => {
				 					showAlert('Customer ID updated for Bank');
				 					await updateKeyPersonCustID.run(
										async (response) => {
				 								showAlert('Customer ID updated for Key Person');
				 								await updateKeyPersonLimitCustID.run(
													async (response) => {
				 											showAlert('Customer ID updated for Key Person');
				 											await updateUBOCustID.run(
																async (response) => {
				 														showAlert('Customer ID updated for UBO');
				 														await updateKYCCustID.run(
																			(response) => {
				 																showAlert('Customer ID updated for KYC Proofs');
				 															} )
																} )
													} )
										} )
							} )
			  } )
			await Helper.clearappsmithStore();
			navigateTo('Application Submission Success', {})
		}
	},
	clearappsmithStore: ()=>{
		for (let key in appsmith.store) {
    if (appsmith.store.hasOwnProperty(key)) {
        const value = appsmith.store[key];
			  if(key=="vayApplicantName"){
					storeValue(key,'Test1402')
				}
        console.log(key, value);
    }
}
	},
	dummy: ()=>{
		return appsmith.store.vayApplicantRole.substring(0,3).toUpperCase()+appsmith.store.customerIsIndForeign+[...Array(8)].map(() => Math.floor(Math.random() * 17).toString(17)).join('');
		//return appsmith.store.vayApplicantRole.substring(0,3).toUpperCase()

	}
}
