		if (Input1.text == "      " ){
				showAlert('Enter First Name')
		}
		else {
			return isAuthSignPersonIndiaExists.run(
		     async (response) => {
				 //if(!!response && !!response.customerEntityPersonOIDForKeyPerson){
				 if(appsmith.store.editKeyPersonOID != ""){
					showAlert('Update to Auth Signatory Person India record::'+ appsmith.store.editKeyPersonOID) 
					await storeValue('customerEntityPersonOIDForAuthSignPerson',response.customerEntityPersonOIDForAuthSignPerson)
					await updateAuthSignatoryIndia.run((response) => showAlert('Auth Signatory Person Details Updated'))
					Helper.updateAuthSignatoryPersonCurrencyLimitQuery();
		  	}else {
					const personOID = utils.generateOID(19);
					//await createAuthSignatoryIndia.run((response) => {showAlert('Key Person Details Added') },(error) => {showAlert('Key Person Details Failed')},{personOID});
					//await createAuthSignCurrencyLimit.run({personOID:personOID});
					//createAuthSignatoryIndia.run((response) => {return createAuthSignCurrencyLimit.run({personOID:personOID})},(error) => {showAlert('Key Person Details Failed')},{personOID});
					const response_key_person = await createAuthSignatoryIndia.run({personOID});
					if (!!response_key_person) {
							showAlert('Auth Signatory Person Details Added') 
							await createAuthSignCurrencyLimit.run({personOID})
							showAlert('Auth Signatory Person Currency Limit Added') 
					}
		  	}
				 	navigateTo('Auth Signatory Summary', {});
		   }
	    );
		} 
	}

