actionConfiguration.body:
() => {
		if (Input1.text == "      " ){
				showAlert('Enter First Name')
		}
		else {
			return isEntityAdminExists.run(
		    async (response) => {
				 //if(!!response && !!response.customerEntityPersonOIDForKeyPerson){
				 if(appsmith.store.editKeyPersonOID != ""){
					showAlert('Update to Entity Admin record::'+ appsmith.store.editKeyPersonOID) 
					await storeValue('customerEntityPersonOIDForEntityAdminPerson ',response.customerEntityPersonOIDForEntityAdminPerson )
					await updateEntityAdmin.run((response) => showAlert('Entity Admin Person Details Updated'))
					Helper.updateEntityAdminCurrencyLimitQuery();
		  	}else {
					const personOID = utils.generateOID(19);
					//await createEntityAdmin.run((response) => {showAlert('Key Person Details Added') },(error) => {showAlert('Key Person Details Failed')},{personOID});
					//await createEntityAdminCurrencyLimit.run({personOID:personOID});
					//createEntityAdmin.run((response) => {return createEntityAdminCurrencyLimit.run({personOID:personOID})},(error) => {showAlert('Key Person Details Failed')},{personOID});
					const response_key_person = await createEntityAdmin.run({personOID});
					if (!!response_key_person) {
							showAlert('Entity Admin Person Details Added') 
							await createEntityAdminCurrencyLimit.run({personOID})
							showAlert('Entity Admin Person Currency Limit Added') 
					}
		  	}
				 	navigateTo('Entity Administrator Summary', {});
		   }
	    );
		} 
	}

