() => {
			if (vaykeyPersonIndType.selectedOptionValue == "      " ){
				showAlert('Select Key Person Type')
		}
		else {
		  return isKeyPersonIndiaExists.run(
		   async (response) => {
				 //if(!!response && !!response.customerEntityPersonOIDForKeyPerson){
				 if(appsmith.store.editKeyPersonOID != ""){
					showAlert('Update to Key Person India record::'+ appsmith.store.editKeyPersonOID) 
					await storeValue('customerEntityPersonOIDForKeyPerson',response.customerEntityPersonOIDForKeyPerson)
					await updateKeyPersonIndia.run((response) => showAlert('Key Person Details Updated'))
					Helper.updateKeyPersonCurrencyLimitQuery();
		  	}else {
					const personOID = utils.generateOID(19);
					//await createKeyPersonIndia.run((response) => {showAlert('Key Person Details Added') },(error) => {showAlert('Key Person Details Failed')},{personOID});
					//await createKeyPersonCurrencyLimit.run({personOID:personOID});
					//createKeyPersonIndia.run((response) => {return createKeyPersonCurrencyLimit.run({personOID:personOID})},(error) => {showAlert('Key Person Details Failed')},{personOID});
					const response_key_person = await createKeyPersonIndia.run({personOID});
					if (!!response_key_person) {
							showAlert('Key Person Details Added') 
							await createKeyPersonCurrencyLimit.run({personOID})
							showAlert('Key Person Currency Limit Added') 
					}
		  	}
				 	navigateTo('Key Person Summary', {});
		   }
	    );

		}
	}

