actionConfiguration.body:
() => {
		if (Input1.text == "      " ){
				showAlert('Enter First Name')
		}
		else {
			return isUBOEntitiesExists.run(
		    async (response) => {
				 //if(!!response && !!response.customerEntityPersonOIDForKeyPerson){
				 if(appsmith.store.editUBOEntitiesOID != ""){
					showAlert('Update to UBO Entities record::'+ appsmith.store.editUBOEntitiesOID) 
					await storeValue('customerEntityForUBOEntities',response.customerEntityForUBOEntities)
					await updateUBOEntities.run((response) => showAlert('UBO Entities Updated'))
					}else {
					const uboEntitiesOID = utils.generateOID(21);
					const response_key_person = await createUBOEntities.run({uboEntitiesOID});
					if (!!response_key_person) {
							showAlert('UBO Entities Added') 
					}
		  	}
				 	navigateTo('UBO Entities Summary', {});
		   }
	    );
		} 
	}

